            lengthCheck = true;
            lastChar = part.charAt(part.length - 1);

            if (!isNaN(num)) {
                numbers++;
                lengthCheck = false;
            }

            if (!lengthCheck || (part.length == 1)) {
                if (lastChar == 'w' || lastChar == 'e') {
                    lat++;
                }

                if (lastChar == 'n' || lastChar == 's') {
                    lon++;
                }
            }

