MapSurfaceTree.prototype.findSurfaceTile = function(id) {
    let tile = this.surfaceTree;

//    for (const lod = 1; lod <= id[0]; lod++) {
//        const mask = 1 << (lod-1);
//        const index = 0;

    for (let lod = id[0]; lod > 0; lod--) {
        const mask = 1 << (lod-1);
        let index = 0;

        if ((id[1] & mask) != 0) {
            index += 1;
        }

        if ((id[2] & mask) != 0) {
            index += 2;
        }

        tile = tile.children[index];

        if (!tile) {
            return null;
        }
    }

    return tile;
};


