ThreeDevice.prototype.getTextSize = function(size, text) {

    const sizeX = size - 1;
    const sizeX2 = Math.round(size*0.5);
    let x = 0;

    for (let i = 0, li = text.length; i < li; i++) {
        const char = text.charCodeAt(i) - 32;

        switch(char) {
        case 12:
        case 14:
        case 27: //:
        case 28: //;7
        case 64: //'
        case 73: //i
        case 76: //l
        case 84: //t
            x += sizeX2;
            break;

        default:
            x += sizeX;
            break;
        }
    }

    return x;
};

