function fillVMapHoles(vmap, mx, my) {
    let holesCount = 0, v;
    let x0, y0, x1, y1, x2, y2;
    let maxX = mx - 1;
    let maxY = my - 1;

    for (let j = 0, lj = my; j < lj; j++) {
        for (let i = 0, li = mx; i < li; i++) {

            v = vmap[j*mx+i];

            if (v === null) {

                //find
                x0 = i - 1;
                y0 = j - 1;

                x1 = i;
                y1 = j;

                x2 = i + 1;
                y2 = j + 1;

                if (x0 < 0) x0 = 0;
                if (y0 < 0) y0 = 0;

                if (x2 > maxX) x2 = maxX;
                if (y2 > maxY) y2 = maxY;

                const vv = [vmap[y0*mx+x0],
                          vmap[y0*mx+x1],
                          vmap[y0*mx+x2],
                          vmap[y1*mx+x0],
                          vmap[y1*mx+x2],
                          vmap[y2*mx+x0],
                          vmap[y2*mx+x1],
                          vmap[y2*mx+x2]];

                let vcount = 0;
                let vsum = 0;

                for (let k= 0; k < 8; k++) {
                    if (vv[k] !== null) {
                        vcount++;
                        vsum += vv[k];
                    }
                }

                if (vcount != 0) {
                    vmap[j*mx+i] = vsum / vcount;
                } else {
                    holesCount++;
                }
            }
        }
    }

    if (holesCount != 0 && holesCount != (mx * my)) {
        fillVMapHoles(vmap, mx, my);
    }
}


function getVMapValue(vmap, x, y, mx, my) {
    x -= 0.5;
    y -= 0.5;

    const maxX = mx - 1;
    const maxY = my - 1;

    if (x < 0) { x = 0; }
    if (y < 0) { y = 0; }
    if (x > maxX) { x = maxX; }
    if (y > maxY) { y = maxY; }

    const ix = Math.floor(x);
    const iy = Math.floor(y);
    const fx = x - ix;
    const fy = y - iy;

    const index = iy * mx;
    const index2 = (iy == maxY) ? index : index + mx;
    const ix2 = (ix == maxX) ? ix : ix + 1;
    const v00 = vmap[index + ix];
    const v01 = vmap[index + ix2];
    const v10 = vmap[index2 + ix];
    const v11 = vmap[index2 + ix2];
    const w0 = (v00 + (v01 - v00)*fx);
    const w1 = (v10 + (v11 - v10)*fx);

    return (w0 + (w1 - w0)*fy);
}


